DROP TABLE IF EXISTS `dhtmlxmenu`;
CREATE TABLE IF NOT EXISTS `dhtmlxmenu` (
  `itemId` text collate utf8_bin NOT NULL,
  `itemParentId` text collate utf8_bin NOT NULL,
  `itemOrder` int(11) NOT NULL default '0',
  `itemText` text collate utf8_bin NOT NULL,
  `itemType` text collate utf8_bin NOT NULL,
  `itemEnabled` int(11) NOT NULL default '0',
  `itemChecked` int(11) NOT NULL default '0',
  `itemGroup` text collate utf8_bin NOT NULL,
  `itemImage` text collate utf8_bin NOT NULL,
  `itemImageDis` text collate utf8_bin NOT NULL,
  KEY `itemOrder` (`itemOrder`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;



